const findAll = async (req,res)=>{
	try {
		const country = await req.context.models.countries.findAll({
			include : [{
				all : true,
				required : true
			}]
		})
		return res.send(country)
	} catch (error) {
		return res.status(404).send(error)
	}
}

const create = async (req,res)=>{
